mapper.update:
 update prize set 
	 name = #{name},stock = #{stock} ,probability=#{probability},photo=#{photoData}
 where id = #{id};

mapper.select:
select id,name,probability,stock,
case when photo is null  then 'false' when photo is not null then 'true' end as photoUrl
from prize order by id desc limit #{startNum},#{size};

mapper.update:
update prize_member 
set member_mobile = #{memberMobile},member_address = #{memberAddress}
where receive_key = #{key} and member_id = #{memberId}
